#include <bits/stdc++.h>

using namespace std;

class Solution {
public:
    bool checkInclusion(const string &s1, const string &s2)
    {
        unordered_map<char, int> need, window;
        for (char c : s1) need[c]++;
        
        int left = 0, right = 0;
        int valid = 0;
        while (right < s2.size()) {
            char c = s2[right];
            right++;
            
            if (need.count(c)) {
                window[c]++;
                if (window[c] == need[c]) {
                    valid++;
                }
                if (valid == need.size()) return true;
            }
            
            if (right - left == s1.size()) {
                char d = s2[left];
                left++;
                if (need.count(d)) {
                    if (window[d] == need[d]) {
                        valid--;
                    }
                    window[d]--;
                }
            }
        }
        
        return false;
    }
};

int main()
{
    Solution sol;
    cout << sol.checkInclusion("vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz",
                               "")
         << endl;
    return 0;
}
